mapper.select:
        SELECT COALESCE(COUNT(*),0) FROM gms_order,gms_venue WHERE gms_venue.venue_id=gms_order.order_venue_id
				AND venue_type = #{catitemname}
				AND order_mktime BETWEEN #{date_st} AND #{date_ed};

SELECT * FROM gms_order,gms_venue,gms_user WHERE gms_order.order_venue_id=gms_venue.venue_id AND gms_user.user_id=gms_order.order_user_id AND (order_user_id LIKE "%" #{query_key} "%" OR venue_name LIKE "%" #{query_key} "%" OR venue_type LIKE "%" #{query_key} "%")
ORDER BY order_date DESC ORDER BY order_date ORDER BY order_price DESC ORDER BY order_price ORDER BY order_mktime DESC ORDER BY order_mktime ORDER BY venue_type
limit #{index},#{currentCount};

        SELECT COALESCE(SUM(order_price),0) FROM gms_order,gms_venue WHERE gms_venue.venue_id=gms_order.order_venue_id
		AND order_mktime BETWEEN #{date_st} AND #{date_ed}
         AND venue_name = #{ven_name} 
         AND venue_type = #{cat_name} 
		;

        SELECT COALESCE(COUNT(*),0) FROM gms_order,gms_venue,gms_user WHERE gms_venue.venue_id=gms_order.order_venue_id
        AND gms_order.order_user_id=gms_user.user_id AND order_mktime BETWEEN #{date_st} AND #{date_ed}
		 AND venue_name = #{ven_name} 
         AND venue_type = #{cat_name} 
         AND user_sex = #{sex} 
         AND user_age < '20' 
         AND user_age >= '20' AND user_age < '29' 
         AND user_age >= '30' AND user_age < '39' 
         AND user_age >= '40' AND user_age < '49' 
         AND user_age >= '50' AND user_age < '59' 
         AND user_age >= '60' 
        ;
